import React, { useState } from 'react'; 

import Footer from "../Componenets/Footer";
import Navbar from "../Componenets/Navbar";

const Login = () => {
  const [email, setEmail] = useState(''); 
  const [password, setPassword] = useState(''); 
  const [submitted, setSubmitted] = useState(false); 

  const validEmail = (email) => {
    // Fungsi validasi email
    // Anda dapat menambahkan logika validasi email sesuai kebutuhan Anda
    return email !== '';
  };

  const handleSubmit = () => {
    if (validEmail(email) && password.length >= 8) {
      setSubmitted(true);
    }
  };

  return (
    <>
      <Navbar />
      <div className="container mt-5">
        <div className="row d-flex justify-content-center">
          <div className="col-md-6">
            <div className="card px-5 py-5" id="form1">
              {submitted ? ( // Jika submitted adalah true, tampilkan pesan sukses
                <div className="success-data">
                  <div className="text-center d-flex flex-column">
                    <i className='bx bxs-badge-check'></i>
                    <span className="text-center fs-1">
                      You have been logged in <br /> Successfully
                    </span>
                  </div>
                </div>
              ) : ( // Jika submitted adalah false, tampilkan form login
                <div className="form-data">
                  <div className="forms-inputs mb-4">
                    <span>Email or username</span>
                    <input
                      autoComplete="off"
                      type="text"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className={`form-control ${!validEmail(email) && emailBlured ? 'is-invalid' : ''}`}
                      onBlur={() => emailBlured = true}
                    />
                    {!validEmail(email) && emailBlured && <div className="invalid-feedback">A valid email is required!</div>}
                  </div>
                  <div className="forms-inputs mb-4">
                    <span>Password</span>
                    <input
                      autoComplete="off"
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                    {password.length < 8 && <div className="invalid-feedback">Password must be 8 characters!</div>}
                  </div>
                  <div className="mb-3">
                    <button className="btn btn-dark w-100" onClick={handleSubmit}>
                      Login
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Login;
